    connect(userId, token) {
        console.log('connect', userId, token)
        if (!self.userId) {
            self.userId = userId;
            let status = this.getConnectionStatus();
            if (status !== ConnectionStatus.ConnectionStatusConnecting && status !== ConnectionStatus.ConnectionStatusLogout) {
                console.log('already connected, no need to connect again');
                self.lastActiveTime = Math.ceil(new Date().getTime() / 1000);
                this.onConnectionChanged(status);
                return false;
            }
        }
        self.userId = userId;

        let lastActiveTime = protoProxy.invoke('connect', userId, token);
        console.log('lastActiveTime', lastActiveTime);
        self.lastActiveTime = lastActiveTime;

        return lastActiveTime;
        // for testing your code
        // self.test();
    }

