    cancelSendingMessage(messageId) {
        let canceled = protoProxy.invoke('cancelSendingMessage', messageId);
        if (!canceled) {
            let xhr = this.uploadingMap.get(messageId);
            if (xhr) {
                this.uploadingMap.delete(messageId);
                try {
                    xhr.abort();
                    canceled = true;
                } catch (e) {
                    console.log(e);
                }
            }
        }
        return canceled;
    }

